import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  Inject,
  Injector,
  OnInit,
  Optional,
} from "@angular/core";
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { PageHeaderType } from "@app/admin/layout/page-header/page-header.component";
import { fnCheckForm } from "@app/utils/forms";
import { NzMessageService } from "ng-zorro-antd/message";
import { NzModalRef, NzModalService } from "ng-zorro-antd/modal";
import { NzTableQueryParams } from "ng-zorro-antd/table";
import { NzUploadFile } from "ng-zorro-antd/upload";
import { finalize, Observable, Observer } from "rxjs";
import {
  PagedListingComponentBase,
  PagedRequestDto,
} from "shared/paged-listing-component-base";

import {
  BrandDto,
  BrandDtoPagedResultDto,
  BrandServiceProxy,
  CreateBrandDto,
  CreateRequestDto,
  FileParameter,
  FileUploaderServiceProxy,
  RequestDto,
  RequestDtoPagedResultDto,
  RequestServiceProxy,
} from "shared/service-proxies/service-proxies";

class PagedCustomerRequestRequestDto extends PagedRequestDto {
  keyword: string;
}

const getBase64 = (file: File): Promise<string | ArrayBuffer | null> =>
  new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

@Component({
  selector: "customer-request",
  templateUrl: "./customer-request.component.html",
  styleUrls: ["./customer-request.component.less"],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class CustomerRequestPage
  extends PagedListingComponentBase<RequestDto>
  implements OnInit
{
  protected delete(entity: RequestDto): void {
    throw new Error("Method not implemented.");
  }
  isVisible = false;
  isDrawerVisible: boolean = false;
  isCustomerRequestFormSaving: boolean = false;
  customerRequestForm: FormGroup;
  confirmModal?: NzModalRef;
  customerRequests = [];
  customerRequest = new RequestDto();
  searchKeyword: string;
  file: NzUploadFile;
  fileList: NzUploadFile[] = [];
  permissionsMap: { [key: string]: boolean } = {};
  previewImage: string | undefined = "";
  previewVisible = false;
  pageHeaderInfo: Partial<PageHeaderType> = {
    breadcrumb: [
      {
        path: "dashboard",
        title: "Home",
      },
      {
        path: "/",
        title: "CustomerRequest",
      },
    ],
  };

  fallback =
    "data:image/png;base64,";

  constructor(
    private injector: Injector,
    private _modalService: NzModalService,
    private _messageService: NzMessageService,
    private _formBuilder: FormBuilder,
    private _cdr: ChangeDetectorRef,
    private _requestService: RequestServiceProxy,
    private _fileUploader: FileUploaderServiceProxy
  ) {
    super(injector);
  }

  ngOnInit(): void {
    this.getDataPage(1);
  }

  onQueryParamsChange(params: NzTableQueryParams): void {
    const { pageIndex, pageSize } = params;
    this.pageNumber = pageIndex;
    this.pageSize = pageSize;
    this.refresh();
  }

  searchValue(event: any) {
    this.searchKeyword = event.target?.value;
    this.refresh();
  }

  list(
    request: PagedCustomerRequestRequestDto,
    pageNumber: number,
    finishedCallback: Function
  ): void {
    request.keyword = this.searchKeyword;
    this._requestService
      .getAll(request.keyword, request.skipCount, request.maxResultCount)
      .pipe(
        finalize(() => {
          finishedCallback();
        })
      )
      .subscribe((result: RequestDtoPagedResultDto) => {
        this.customerRequests = result.items;
        this._cdr.markForCheck();
        this.showPaging(result, pageNumber);
      });
  }

  refreshPage() {
    this.refresh();
  }

  beforeUpload = (
    file: NzUploadFile,
    _fileList: NzUploadFile[]
  ): Observable<boolean> =>
    new Observable((observer: Observer<boolean>) => {
      const isJpgOrPng =
        file.type === "image/jpeg" || file.type === "image/png";
      if (!isJpgOrPng) {
        this._messageService.error("You can only upload JPG file!");
        observer.complete();
        return;
      }
      const isLt2M = file.size! / 1024 / 1024 < 2;
      if (!isLt2M) {
        this._messageService.error("Image must smaller than 2MB!");
        observer.complete();
        return;
      }
      observer.next(isJpgOrPng && isLt2M);
      this.file = file;
      observer.complete();
    });

  getFileUrl({ file }): void {
    const status = file.status;
    if (status === "done") {
      this.customerRequestForm.get("logo").setValue(file.response.result.url);
    } else if (status === "error") {
      this._messageService.error(`${file.name} file upload failed.`);
    }
  }

  handlePreview = async (file: NzUploadFile): Promise<void> => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj!);
    }
    this.previewImage = file.url || file.preview;
    this.previewVisible = true;
  };

  handleRemove = (file: any) =>
    new Observable<boolean>((obs) => {
      file.url = "";

      this.customerRequestForm.get("logo").setValue(file.url);
      obs.next(false);
    });

  showModal(): void {
    this.isVisible = true;
  }

  handleOk(): void {
    console.log("Button ok clicked!");
    this.isVisible = false;
  }

  handleCancel(): void {
    console.log("Button cancel clicked!");
    this.isVisible = false;
  }
}
